class Solution:
    def __init__(self) -> None:
        pass

    def isSubsequence(self, s: str, t: str) -> bool:
        """
        This will use two pointers to iterate over substring s and string t.
        If a character in s matches a character in t, then both pointers will increase.
        If not, then only the pointer iterating over t will increase. This will continue
        until either pointer runs out of bounds.

        If all the characters in s are found in t, then the pointer iterating over s should
        equal the length of s. Otherwise, s is not a substring of t.
        
        """

        i, j = 0, 0
        while i < len(s) and j < len(t):
            if s[i] == t[j]:
                i+=1
            j+=1
        return i == len(s)



s = Solution()

s1, t1 = "abc", "ahbgdc"
s2, t2 = "axc", "ahbgdc"
s3, t3 = "bcd", ""

print(s.isSubsequence(s1, t1))
print(s.isSubsequence(s2, t2))
print(s.isSubsequence(s3, t3))

